package parser

import (
	"bytes"
	"encoding/hex"
	"strings"
	"testing"
)

func TestTTLVReader(t *testing.T) {
	b := bytes.Buffer{}
	d,_ := hex.DecodeString("")
	b.Write(d)
	out,_ := TTLVReader(&b)
	if strings.Compare( strings.ToUpper(hex.EncodeToString(out)) , strings.ToUpper("")) != 0 {
		t.Log(strings.ToUpper(hex.EncodeToString(out)))
		t.Log("")
		t.Fail()
	}
}
